import React from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link } from "react-router-dom";
import logo from "../../assets/images/logo.png";
import Register from "./../../pages/Register";

const nav__links = [
  { path: "/home", display: "Home" },
  { path: "/#", display: "About" },
  { path: "/tours", display: "Tours" },
];

const Header = () => {
  return (
    <header className="header">
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-item-center justify-content-between">
            {/*================LOGO=================== */}
            <div className="logo">
              <img src={logo} alt="logo" />
            </div>
            {/*================LOGO END=================== */}

            {/*================MENU=================== */}
            <div className="navigation">
              <ul className="menu d-flex align-item-center gap-5">
                {nav__links.map((item, index) => {
                  return (
                    <li className="nav__item" key={index}>
                      <NavLink to={item.path}>{item.display}</NavLink>
                    </li>
                  );
                })}
              </ul>
            </div>
            {/*================MENU END=================== */}
            <div className="nav__right d-flex align-item-center gap-4">
              <div className="nav__btns d-flex align-item-center gap-4">
                <Button>
                  <Link className="btn secondary__btn" to="/login">
                    Login
                  </Link>
                </Button>
                <Button>
                  <Link className="btn primary" to="/register">
                    Register
                  </Link>
                </Button>
              </div>

              <span className="mobile__menu">
                <i class="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
